import React from 'react';
import PropTypes from 'prop-types';

class App extends React.Component {
  state = {
    isLoading: true,
    films: [],
  };

  componentDidMount() {
    setTimeout(() => {
      this.setState({ isLoading: false });
    }, 6000);
  }

  render() {
    const { isLoading } = this.state;
    return <div> {isLoading ? 'Loading..' : 'Страница загружена'}</div>;
  }
}

export default App;
