  state = {
    isLoading: true,
    films: [],
  };

  getFilms = async () => {
    const {
      data: {
        data: { films },
      },
    } = await axios.get(
      'https://yts.mx/api/v2/list_movies.json?sort_by=rating'
    );
    this.setState({ films, isLoading: false });
  };
  componentDidMount() {
    this.getFilms();
  }
