    label: 'Comedy',
  },
];

function Header() {
  return (
    <header className="head">
      <nav className="navigation">
        <div className="logo">
          <a
            className="logotype"
            onClick={(e) => {
              e.preventDefault();
              window.scroll(0, 0);
            }}
          >
            KinoLux
          </a>
        </div>
        <ul className="nav-list">
          {Menu.map((item, index) => (
            <li className="list-item" key={index}>
              <NavLink to={item.path}>{item.label}</NavLink>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}

export default Header;
