function Food({ name, picture }) {
  return (
    <div>
      <h3>I love {name}</h3>
      <img src={picture} alt="" />
    </div>
  );
}

const foodIlike = [
  {
    name: 'soup',
    image:
      'https://static.onecms.io/wp-content/uploads/sites/44/2021/05/28/spaghetti-squash-soup.jpg',
  },
  {
    name: 'sanwich',
    image:
      'https://insanelygoodrecipes.com/wp-content/uploads/2021/03/Homemade-Grilled-Cheese-Sandwich-with-Tomatoes-500x500.png',
  },
  {
    name: 'meat',
    image:
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRsjCCaSshaSGGZCxLhifabxbwoIeQebYL4Vw&usqp=CAU',
  },
];

function renderFood(dish) {
  return <Food name={dish.name} picture={dish.image} />;
}

function App() {
  return <div>{foodIlike.map(renderFood)}</div>;
}

export default App;
